insert into cars (make, color, model, price, year, details) values ('Audi', 'White', 'Raptor', 150000, 2015,"Introducing the Audi Raptor, the apex predator of the automotive world and the epitome of high-performance sports cars. Born on the racetrack and engineered to dominate the roads, this exceptional machine combines cutting-edge technology, breathtaking speed, and unrivaled agility. The Audi Raptor is the ultimate manifestation of Audi's relentless pursuit of auto sports excellence.");